import React from 'react'
import '../App.css'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
function NotePage() {
  const params = useParams()
  const [note, setNote] = useState({})
  useEffect(() => {
    const get_note = async () => {
      let res = await fetch('/notes/' + params.id)
      if (res.status === 200) {
        let data = await res.json()
        setNote(data)
      }
    }
    get_note()
  }, [params])
  useEffect(() => {
    console.log(note)
    fetch(`http://127.0.0.1:8000/notes/${note.id}/update`,{
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(note)
    })
  }, [note])
  return (
    <div className="Note">
      <h2 className='title'>{note.name}</h2>
      <textarea onChange={(e) => {
        note.note = e.target.value
        setNote(note)
        console.log(note)
      }} defaultValue={note.note} className="content">
      </textarea>
      <span className='created'>{note.created}</span>
      <span className='updated'>{note.updated}</span>
    </div>
  )
}

export default NotePage
