import React, {useState, useEffect } from 'react'

function CreateNewNote() {
    const [title,setTitle] = useState('')
    const [note, setNote] = useState('')
    const [available, setAvilable] = useState(false)
    function handleSumbit() {
        if (title === '' || note === '') {
            setAvilable(true)
        }
    }
    return (
        <form onSubmit={handleSumbit} className='create-note'>
            <div className="title">
                <label htmlFor="title">
                    title:
                </label>
                <input onChange={(e) => {
                    setTitle(e.target.value)
                }} type="text" name="title" placeholder='Enter title of the note' />
            </div>
            <div className="note">
                <label htmlFor="note">
                    note:
                </label>
                <textarea onChange={(e) => {
                    setNote(e.target.value)
                }} rows='4' type="text" name="note" placeholder='Enter your note'/>
            </div>
            {available && (
                <p style={{color:'red'}}>you can't let title or note empty.</p>
            )}
            <input type="submit" value="add" />
        </form>
    )
}

export default CreateNewNote
